mapper.select:
SELECT CART_NUMBER
FROM TBL_CART
WHERE USER_NUMBER=#{userNumber} AND PRODUCT_NUMBER=#{productNumber}

mapper.update:
UPDATE TBL_CART
SET CART_COUNT = CART_COUNT + #{cartCount}
WHERE CART_NUMBER = #{cartNumber}

UPDATE TBL_CART
SET CART_COUNT = #{cartCount}
WHERE CART_NUMBER = #{cartNumber}

UPDATE TBL_CART
SET CART_COUNT = #{cartCount} - CART_COUNT
WHERE CART_NUMBER = #{cartNumber}
